int g(int a,int b){return b?g(b,a%b):(a?a:1);} int l(int a,int b){return a*b/g(a,b);} int r(float n){return n>0?(int)(n+0.5):(int)(n-0.5);} int ab(int a){return a<0?-a:a;} int printf(const char *fmt,...); int scanf(const char *fmt,...); int main(void){int a,b,c,d,u,v,f; char o; float x=0,lhs,rhs; scanf("%d/%d %c %d/%d",&a,&b,&o,&c,&d); lhs=(float)a/b; rhs=(float)c/d; switch(o){case '+':x=lhs+rhs;break;case '-':x=lhs-rhs;break;case '*':x=lhs*rhs;break;case '/':x=lhs/rhs;} f=ab(g(r(x*b*d),b*d)); u=r(x*b*d/f); v=b*d/f; if(u==0)printf("%d/%d %c %d/%d = 0\n",a,b,o,c,d); else if(v==1)printf("%d/%d %c %d/%d = %d\n",a,b,o,c,d,u); else printf("%d/%d %c %d/%d = %d/%d\n",a,b,o,c,d,u,v); return 0;}